import { FormControl, FormGroup, Validators } from "@angular/forms";
import { Component, OnInit } from "@angular/core";
import {Router} from '@angular/router';

import { UsuarioService } from "../services/usuario.service";
import { usuario } from "../models/user";

@Component({
  selector: 'app-registro',
  templateUrl: './registro.component.html',
  styleUrls: ['./registro.component.css']
})
export class RegistroComponent implements OnInit {

  credentialsInvalid: boolean = false;
  messageError: string = '';

  myForm = new FormGroup({
    userName: new FormControl("", [
      Validators.required,
      Validators.minLength(4),
      Validators.maxLength(15)
    ]),
    userLastName: new FormControl("", [
      Validators.required,
      Validators.minLength(4),
      Validators.maxLength(30)
    ]),
    userPass: new FormControl("", [
      Validators.required,
      Validators.minLength(4),
      Validators.maxLength(15)
    ]),
    userConfirmPass: new FormControl("", [
      Validators.required,
      Validators.minLength(4),
      Validators.maxLength(15)
    ]),
    userImag: new FormControl(""),
  });

  constructor(private registerService: UsuarioService, private router:Router) { }

  ngOnInit(): void {
  }

  onSubmit(): void {
    const user = this.myForm.value;
    if(user.userName === '' || user.userLastName === '' || user.userPass === '' || user.userConfirmPass === '' ){
      this.credentialsInvalid = true;
      this.messageError = 'Faltan campos a ingresar';
      setTimeout(() => {this.credentialsInvalid = false;},3500);
      return;
    }else if(user.userPass !== user.userConfirmPass) {
      this.credentialsInvalid = true;
      this.messageError = 'Contraseñas no coinciden';
      setTimeout(() => {this.credentialsInvalid = false;},3500);
      return;
    }
    const usuario: usuario = {
      userName:       user.userName,
      nombre:         user.userLastName,
      password:       user.userPass,
      imag:           this.imag64
    }
    console.log('***' ,usuario);
    this.registerService.postRegistro(usuario).subscribe((data: {})=> {
      console.log(data, 'adentro');
      this.router.navigateByUrl('/login');
    }, error => {
      this.credentialsInvalid = true;
      this.messageError = 'Problemas en la conexión';
      console.log(error, 'error');
      setTimeout(() => {this.credentialsInvalid = false;},3500);
    })
  }
  
  imagen: string = '../../assets/plantillaPerfil.jpg';
  imag64: string = `
  
  `;
  handleUpload(event) {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
        // console.log(reader.result);
        const imagen: string = reader.result.toString();
        const sinMetadata: string = imagen.split(';base64,')[1];
        // console.log('***********',sinMetadata)
        this.imagen = reader.result.toString();
        this.imag64 = sinMetadata;
    };
}
  
}
